head.script:
function toggleMenu(){
  var m = document.getElementById('main-nav');
  if(!m) return;
  if(m.style.display === 'block') m.style.display = 'none';
  else m.style.display = 'block';
}
function toggleSearch(){
  var s = document.getElementById('search-box');
  if(!s) return;
  if(s.style.display === 'block') s.style.display = 'none';
  else s.style.display = 'block';
}
// Close mobile nav on resize (simple)
window.addEventListener('resize', function(){ if(window.innerWidth>980){ var m=document.getElementById('main-nav'); if(m) m.style.display='block'; }});
