 function strip(html)
    {
      var tmp = document.createElement("DIV");
      tmp.innerHTML = html;
      return tmp.textContent || tmp.innerText || "";
    }
  function getSapo(html) {
		return strip(html).substring(0, 96) + '...';
  }
  function getImgSrc(html, vdefault) {
	var tmp = document.createElement("DIV");
      tmp.innerHTML = html;
		var imgs = tmp.getElementsByTagName('img');
if (imgs.length > 0) return imgs.item(0).src;
    else return (typeof vdefault === 'undefined') ? 'http://placehold.it/600x400' : vdefault;// NO IMAGE
  }
