  function echoRelated(json) {
var html = '';
    for (var i = 0; i < json.feed.entry.length; i++) {
      for (var j = 0; j < json.feed.entry[i].link.length; j++) {
        if (json.feed.entry[i].link[j].rel == 'alternate') {
          var postUrl = json.feed.entry[i].link[j].href;
          break;
        }
      }
      var postTitle = json.feed.entry[i].title.$t;
      //var postSummary = json.feed.entry[i].summary.$t;
      var postContent = json.feed.entry[i].content.$t;
var postImage = getImgSrc(postContent, 'http://placehold.it/330x165');
var postTimeAGo = jQuery.timeago(new Date(json.feed.entry[i].published.$t));

      var item = '<div class="related-item"><div class="post-thumbnail"><a href="'+postUrl+'"><img width="330" height="165" alt="2016-04-02_084328" style="width: 330px; height: 165px" class="attachment-tie-medium wp-post-image" src="'+postImage+'"/>						<span </span></a></div><!-- post-thumbnail /--><h3><a rel="bookmark" href="'+postUrl+'">'+postTitle+'</a></h3><p class="post-meta"><span class="tie-date"><i class="fa fa-clock-o"></i>'+postTimeAGo+'</span></p></div>';
      html += item;
    }

html = '<section id="related_posts">' + 
'	<div class="block-head">' + 
'		<h3>BÀI VIẾT LIÊN QUAN</h3><div class="stripe-line"></div>' + 
'	</div>' + 
'	<div class="post-listing">' + 
'		' + html + 
'		<div class="clear"></div>' + 
'	</div>' + 
'</section>';
document.write(html);

  }
