  function echoHomeBox(json) {
var html = '';
var stt = 1;
    for (var i = 0; i < json.feed.entry.length; i++) {
      for (var j = 0; j < json.feed.entry[i].link.length; j++) {
        if (json.feed.entry[i].link[j].rel == 'alternate') {
          var postUrl = json.feed.entry[i].link[j].href;
          break;
        }
      }

      var postId = json.feed.entry[i].id.$t;
      var postTitle = json.feed.entry[i].title.$t;
      var c = postContent = json.feed.entry[i].content.$t;

var postImage = 'http://placehold.it/110x75';
var postSummary = getSapo(c);
      if (stt++ > 1) {
postImage = getImgSrc(c, 'http://placehold.it/110x75');
      html += "<li class='other-news'><div class='post-thumbnail'><a href='"+postUrl+"' rel='bookmark'><img alt='10665142_872439649435476_3317243975175011413_n' class='attachment-tie-small wp-post-image' src='"+ postImage +"'width='110' height='75' /><span /></a></div><!-- post-thumbnail /--><h3 class='post-box-title'><a href='"+postUrl+"' rel='bookmark'>"+postTitle+"</a></h3><p class='post-meta'></p></li>";
                   } else {
postImage = getImgSrc(c, 'http://placehold.it/310x165');
html += "<li class='first-news'><div class='post-thumbnail'><a href='"+postUrl+"' rel='bookmark'><img alt='2016-04-02_084328' class='attachment-tie-medium wp-post-image' height='165' src='"+postImage+"'/>									<span ay-icon'/></a></div><!-- post-thumbnail /--><h2 class='post-box-title'><a href='"+postUrl+"' rel='bookmark'>"+postTitle+"</a></h2><p class='post-meta'></p><div class='entry'><p>"+postSummary+"</p><a class='more-link' href='"+postUrl+"'>Xem tiếp</a></div></li>";
