  function mycallback(json) {
var html = '';
    for (var i = 0; i < json.feed.entry.length; i++) {
      for (var j = 0; j < json.feed.entry[i].link.length; j++) {
        if (json.feed.entry[i].link[j].rel == 'alternate') {
          var postUrl = json.feed.entry[i].link[j].href;
          break;
        }
      }

      var postId = json.feed.entry[i].id.$t;
if (postId.indexOf(HP_CURRENT_SINGLE_ID) != -1) continue;
      var postTitle = json.feed.entry[i].title.$t;
      //var postSummary = json.feed.entry[i].summary.$t;
      var c = postContent = json.feed.entry[i].content.$t;


var postTimeAGo = jQuery.timeago(new Date(json.feed.entry[i].published.$t));
var postImage = getImgSrc(c, 'http://placehold.it/110x75');

html += '<li><div class="post-thumbnail tie-appear"><a rel="bookmark" href="'+postUrl+'"><img src="'+postImage+'" class="attachment-tie-small wp-post-image tie-appear" alt="2016-04-02_084328" height="75" width="110"><span ></span></a></div><!-- post-thumbnail /--><h3><a href="'+postUrl+'">'+postTitle+'</a></h3><span class="tie-date"><i class="fa fa-clock-o"></i>'+postTimeAGo+'</span></li>';




    }
document.getElementById("ul-tin-moi-nhat").innerHTML = html;


  }
