  function mycallback(json) {
var html = '';
var arrMonth = ['Tháng Một', 'Tháng Hai', 'Tháng Ba', 'Tháng Tư', 'Tháng Năm', 'Tháng Sáu', 'Tháng bẩy', 'Tháng Tám', 'Tháng Chín', 'Tháng Mười', 'Tháng Mười Một', 'Tháng Mười Hai'];
    for (var i = 0; i < json.feed.entry.length; i++) {
      for (var j = 0; j < json.feed.entry[i].link.length; j++) {
        if (json.feed.entry[i].link[j].rel == 'alternate') {
          var postUrl = json.feed.entry[i].link[j].href;
          break;
        }
      }

      var postId = json.feed.entry[i].id.$t;
      var postTitle = json.feed.entry[i].title.$t;
      var c = postContent = json.feed.entry[i].content.$t;


//var postTimeAGo = jQuery.timeago(new Date(json.feed.entry[i].published.$t));
var postImage = getImgSrc(c, 'http://placehold.it/110x75');
var postDate = new Date(json.feed.entry[i].published.$t);
var postDMY = postDate.getDate() + ' ' + arrMonth[postDate.getMonth()] + ', ' + postDate.getFullYear();

html = '<li><div class="post-thumbnail tie-appear"><a href="'+postUrl+'" rel="bookmark"><img width="110" height="75" src="'+postImage+'" class="attachment-tie-small wp-post-image tie-appear" alt="2016-02-03_212005"><span></span></a></div><!-- post-thumbnail /--><h3><a href="'+postUrl+'">'+postTitle+'</a></h3><span class="tie-date"><i class="fa fa-clock-o"></i>'+postDMY+'</span></li>';




    }
document.write(html);


  }
