if (shortcodesParaProcessar.length > 0) {
shortcodesParaProcessar.forEach(match => {
const fullString = match[1];
const opcoesArray = fullString.split('|');
opcoesArray.shift();
if (opcoesArray.length > 0) {
const selectElement = document.createElement('select');
selectElement.className = 'variacaoProdutoBP';
opcoesArray.forEach(textoOpcao => {
const textoLimpo = textoOpcao.trim();
if (textoLimpo) {
const optionElement = document.createElement('option');
optionElement.value = textoLimpo;
optionElement.textContent = textoLimpo;
selectElement.appendChild(optionElement)}});
containerOpcoes.appendChild(selectElement)}});
