function removeHtmlTag(e, t) {
  for (var n = e.split("<"), r = 0; r < n.length; r++) - 1 != n[r].indexOf(">") && (n[r] = n[r].substring(n[r].indexOf(">") + 1, n[r].length));
  n = n.join("");
  return n.substring(0, t - 1)
}
