function Cart() {
  var e = this;
  e.nextId = 1;
  e.Version = "2.2";
  e.Shelf = null;
  e.items = {};
  e.isLoaded = false;
  e.pageIsReady = false;
  e.quantity = 0;
  e.total = 0;
  e.taxRate = 0;
  e.taxCost = 0;
  e.shippingFlatRate = 0;
  e.shippingTotalRate = 0;
  e.shippingQuantityRate = 0;
  e.shippingRate = 0;
  e.shippingCost = 0;
  e.currency = IDR;
  e.checkoutTo = PayPal;
  e.email = "tuanbagues@gmail.com";
  e.merchantId = "";
  e.successUrl = "/";
  e.cancelUrl = null;
  e.entry = "";
  e.entryURL = "";
  e.entryDelete = false;
  e.cookieDuration = 30;
  e.storagePrefix = "sc_";
  e.MAX_COOKIE_SIZE = 4e3;
  e.cartHeaders = ["thumb_image", "Name","Price", "decrement", "Quantity", "increment", "total", "Remove"];
  if (e.entryDelete == true) {
    this.items = {}
  }
  e.add = function (e) {
    var t = this;
    if (!t.pageIsReady) {
      t.initializeView();
      t.update()
    }
    if (!t.isLoaded) {
      t.load();
      t.update()
    }
    var n = new CartItem;
    if (!arguments || arguments.length === 0) {
      return null
    }
    var r = arguments;
    if (e && typeof e !== "string" && typeof e !== "number") {
      r = e
    }
    n.parseValuesFromArray(r);
    n.checkQuantityAndPrice();
    if (t.hasItem(n)) {
      var i = t.hasItem(n);
      i.quantity = parseInt(i.quantity, 10) + parseInt(n.quantity, 10);
      n = i
    } else {
      t.items[n.id] = n
    }
    t.update();
    $("html, body")
    .animate({
      scrollTop: 0
    }, 1e3);
    return n
  };
  e.remove = function (t) {
    var n = {};
    e.each(function (e) {
      if (e.id !== t) {
        n[e.id] = e
      }
    });
    this.items = n
  };
  e.empty = function () {
    simpleCart.items = {};
    simpleCart.update()
  };
  e.find = function (t) {
    if (!t) {
      return null
    }
    var n = [];
    e.each(function (r, i, s) {
      fits = true;
      e.each(t, function (e, t, n) {
        if (!r[n] || r[n] != e) {
          fits = false
        }
      });
      if (fits) {
        n.push(r)
      }
    });
    return n.length === 0 ? null : n
  };
  e.each = function (t, n) {
    var r, i = 0
    , s;
    if (typeof t === "function") {
      var o = t;
      u = e.items
    } else if (typeof n === "function") {
      var o = n
      , u = t
      } else {
        return
      }
    for (r in u) {
      if (typeof u[r] !== "function") {
        s = o.call(e, u[r], i, r);
        if (s === false) {
          return
        }
        i++
      }
    }
  };
  e.chunk = function (e, t) {
    if (typeof t === "undefined") {
      t = 2
    }
    var n = e.match(RegExp(".{1," + t + "}", "g"));
    return n || []
  };
  e.checkout = function () {
    if (simpleCart.quantity === 0) {
      error("Cart is empty");
      return
    }
    switch (simpleCart.checkoutTo) {
      case PayPal:
        simpleCart.paypalCheckout();
        break;
      case GoogleCheckout:
        simpleCart.googleCheckout();
        break;
      case Email:
        simpleCart.emailCheckout();
        break;
      default:
        simpleCart.customCheckout();
        break
    }
  };
  e.paypalCheckout = function () {
    var e = this
    , t = "scrollbars,location,resizable,status"
    , n = "https://www.paypal.com/cgi-bin/webscr?cmd=_cart" + "&upload=1" + "&business=" + e.email + "&currency_code=" + e.currency
    , r = 1
