      i = i + "&return=" + e.successUrl
    }
    if (e.cancelUrl) {
      i = i + "&cancel_return=" + e.cancelUrl
    }
    n = n + i;
    window.open(n, "paypal", t)
  };
  e.googleCheckout = function () {
    var e = this;
    if (e.currency !== USD && e.currency !== GBP) {
      error("Google Checkout only allows the USD and GBP for currency.");
