jQuery(function ($) {
  $("#btn-email").click(function () { 
    var email = $("#email-register").val();
    if (email.length <= 0)
    {
      $("#email-register").focus();
      $("#email-register").css({ "border": "1px solid red"})
      return false;
    }
    else
    {
      $.ajax({
        type: 'POST',
        url: '/Footer/RegisterEmail',
        data: { email: email },
        success: function (result) {
          alert(result);
          $("#email-register").val("");
        }
      });
    }
  });
});      
