$(document).ready(function () {
  $(".chek-guarantee").click(function(){
    var href = $(this).attr("data-href");
    window.location.href = href;
  });
  $(".mif-display").click(function () {
    var href = $(this).attr("data-href");
    //alert(href);
    $.ajax({
      type:'GET',
      url: href,
      success: function (result) {
      window.location.href = result.url;
    }
           })
  });
});

