(function(){

function loadGrid(label, targetId){
  var url = '/feeds/posts/default/-/' + encodeURIComponent(label) +
            '?alt=json&max-results=8';

  fetch(url)
    .then(function(res){ return res.json(); })
    .then(function(data){
      var html = '';
      if(!data.feed || !data.feed.entry) return;

      data.feed.entry.forEach(function(post){
        var title = post.title.$t;
        var link  = '';
        var img   = '';

        post.link.forEach(function(l){
          if(l.rel === 'alternate'){ link = l.href; }
        });

        if(post.media$thumbnail){
          img = post.media$thumbnail.url.replace('s72','s300');
        } else {
          img = 'https://via.placeholder.com/400x250?text=News';
        }

html +=
          '<article class="gtn-card">' +
            '<a href="' + link + '">' +
              '<img src="' + img + '" alt="' + title.replace(/"/g,'') + '"/>' +
              '<div class="gtn-card-body">' +
                '<div class="gtn-card-title">' + title + '</div>' +
              '</div>' +
            '</a>' +
          '</article>';
      });

      document.getElementById(targetId).innerHTML = html;
    });
}

loadGrid('Terkini','gtnGridTerkini');
loadGrid('Nasional','gtnGridNasional');
loadGrid('Teknologi','gtnGridTeknologi');
loadGrid('Ekonomi','gtnGridEkonomi');

})();
