(function(){
  var LABEL = "News";
  var feedUrl = "/feeds/posts/default/-/" + encodeURIComponent(LABEL) + "?alt=json&max-results=10";

  fetch(feedUrl)
    .then(function(res){ return res.json(); })
    .then(function(data){
      var wrap = document.getElementById("gtTrendingWrap");
      wrap.innerHTML = "";

      if(!data.feed || !data.feed.entry){
        wrap.innerHTML = "<span class='gt-loading'>Belum ada data</span>";
        return;
      }

      data.feed.entry.forEach(function(entry, index){
        var title = entry.title.$t;
        var link = "";

        for(var i=0;i<entry.link.length;i++){
          if(entry.link[i].rel === "alternate"){
            link = entry.link[i].href;
            break;
          }
        }

        var a = document.createElement("a");
        a.href = link;
        a.textContent = title;

        // item pertama aktif
        if(index === 0){
          a.classList.add("active");
  }

        // klik ‚Üí aktif pindah
        a.addEventListener("click", function(){
          var all = wrap.querySelectorAll("a");
          all.forEach(function(el){
            el.classList.remove("active");
          });
          this.classList.add("active");
        });

        wrap.appendChild(a);
      });

      // AUTO SCROLL
      setInterval(function(){
        wrap.scrollLeft += 220;
        if(wrap.scrollLeft >= wrap.scrollWidth - wrap.clientWidth){
          wrap.scrollLeft = 0;
        }
      }, 4000);
    })
    .catch(function(){
      document.getElementById("gtTrendingWrap").innerHTML =
        "<span class='gt-loading'>Gagal memuat</span>";
    });
})();
//

//
(function(){
  var LABEL = "News";
  var FEED = "/feeds/posts/default/-/" + encodeURIComponent(LABEL) + "?alt=json&max-results=10";

  var trendingWrap = document.getElementById("gtTrendingWrap");
  var heroTitle = document.getElementById("gtHeroTitle");
  var heroImg = document.getElementById("gtHeroImg");
  var heroLink = document.getElementById("gtHeroLink");

  fetch(FEED)
    .then(res => res.json())
    .then(data => {
      trendingWrap.innerHTML = "";
      if(!data.feed || !data.feed.entry) return;

      data.feed.entry.forEach((entry, i) => {
        var title = entry.title.$t;
        var link = entry.link.find(l => l.rel === "alternate").href;

        var img = "";
        if(entry.media$thumbnail){
          img = entry.media$thumbnail.url.replace("s72","s800");
        }

        // === SET HERO PERTAMA ===
        if(i === 0){
          heroTitle.textContent = title;
          heroLink.href = link;
          if(img) heroImg.src = img;
        }



    // === TRENDING ITEM ===
        var a = document.createElement("a");
        a.textContent = title;
        a.href = "javascript:void(0)";
        if(i === 0) a.classList.add("active");

        a.onclick = function(){
          document.querySelectorAll(".gt-trending-list a")
            .forEach(el => el.classList.remove("active"));

          this.classList.add("active");
          heroTitle.textContent = title;
          heroLink.href = link;
          if(img) heroImg.src = img;
        };

        trendingWrap.appendChild(a);
      });
    });
})();
