document.addEventListener('DOMContentLoaded', function () {

  var sections = document.querySelectorAll('.gt-auto-section');

  for (var s = 0; s < sections.length; s++) {

    var section = sections[s];
    var label = section.getAttribute('data-label');
    var main = section.querySelector('.gt-main-slide');
    var list = section.querySelector('.gt-latest-scroll');
    var seeAll = section.querySelector('.gt-see-all');

    if (!label || !main || !list) continue;

    if (seeAll) {
      seeAll.href = '/search/label/' + encodeURIComponent(label);
    }

    var feedUrl =
      location.origin +
      '/feeds/posts/default/-/' +
      encodeURIComponent(label) +
      '?alt=json&max-results=6';

    fetch(feedUrl)
      .then(function (res) { return res.json(); })
      .then(function (data) {

        if (!data.feed || !data.feed.entry) return;

        var posts = data.feed.entry;

        /* === POST UTAMA === */
        var p = posts[0];
        var title = p.title.$t;
        var link = '';
        for (var i = 0; i < p.link.length; i++) {
          if (p.link[i].rel === 'alternate') {
            link = p.link[i].href;
            break;
          }
        }

        var img = p.media$thumbnail
          ? p.media$thumbnail.url.replace('s72', 's640')
          : 'https://via.placeholder.com/640x360';

        main.innerHTML =
          '<a href="' + link + '">' +
            '<img src="' + img + '" loading="lazy" />' +
            '<div class="gt-main-caption">' +
              '<span class="gt-cat">' + label + '</span>' +
              '<h2>' + title + '</h2>' +
            '</div>' +
          '</a>';

        /* === LIST KECIL === */
        var html = '';
        for (var j = 1; j < posts.length; j++) {

          var post = posts[j];
          var t = post.title.$t;
          var l = '';

          for (var k = 0; k < post.link.length; k++) {
            if (post.link[k].rel === 'alternate') {
              l = post.link[k].href;
              break;
            }
          }

          var im = post.media$thumbnail
            ? post.media$thumbnail.url.replace('s72', 's240')
            : 'https://via.placeholder.com/240x135';

          html +=
            '<a class="gt-latest-card" href="' + l + '">' +
              '<img src="' + im + '" loading="lazy" />' +
              '<h4>' + t + '</h4>' +
            '</a>';
        }

        list.innerHTML = html;

      });

  }

});
