      var label = section.getAttribute('data-label');
      var track = section.querySelector('.js-feed');
      if (!label || !track) return;

      var url = location.origin + '/feeds/posts/default/-/' + label + '?alt=json&max-results=8';

      fetch(url)
        .then(function(r){ return r.json(); })
        .then(function(d){
          if (!d.feed || !d.feed.entry) return;

          var html = '';
          for (var i = 0; i < d.feed.entry.length; i++) {
            var p = d.feed.entry[i];
            var title = p.title.$t;
            var link = p.link.filter(function(l){ return l.rel === 'alternate'; })[0].href;
            var img = p.media$thumbnail
              ? p.media$thumbnail.url.replace('s72','s480')
              : 'https://via.placeholder.com/480x270';

            html +=
              '<article class="gt-post">' +
                '<a href="' + link + '">' +
                  '<img src="' + img + '" loading="lazy"/>' +
                  '<h5>' + title + '</h5>' +
                '</a>' +
              '</article>';
          }

          track.innerHTML = html;

          var index = 0;
          setInterval(function(){
            if (track.children.length < 2) return;
            index = (index + 1) % track.children.length;
            track.scrollLeft = track.children[index].offsetLeft;
          }, 5000);

        });
    })(grids[s]);
  }

});
