(function () {
  var box = document.querySelector('.gt-headline');
  if (!box) return;

  var label = box.getAttribute('data-label');
  var feed = '/feeds/posts/default/-/' + label + '?alt=json&max-results=1';

  fetch(feed)
    .then(function (r) { return r.json(); })
    .then(function (data) {
      if (!data.feed || !data.feed.entry) return;

      var post = data.feed.entry[0];
      var title = post.title.$t;
      var link = '';

      for (var i = 0; i < post.link.length; i++) {
        if (post.link[i].rel === 'alternate') {
          link = post.link[i].href;
          break;
        }
      }

      var img = 'https://via.placeholder.com/800x450';
      if (post.media$thumbnail) {
        img = post.media$thumbnail.url.replace('s72', 's800');
      }

      var published = new Date(post.published.$t);
      var now = new Date();
      var diff = Math.floor((now - published) / 60000);

      var timeAgo = '';
      if (diff < 60) {
        timeAgo = diff + ' menit lalu';
      } else if (diff < 1440) {
        timeAgo = Math.floor(diff / 60) + ' jam lalu';
      } else {
        timeAgo = Math.floor(diff / 1440) + ' hari lalu';
      }

      box.innerHTML =
        '<a href="' + link + '">' +
          '<img loading="lazy" src="' + img + '" />' +
          '<span class="gt-badge">' + label.toUpperCase() + '</span>' +
          '<span class="gt-time">' + timeAgo + '</span>' +
          '<h2>' + title + '</h2>' +
        '</a>';
    });
})();
