    var theme_list_open = false;
    $(document).ready(function() {
      function e() {
        var e = $("#switcher")
          .height();
        $("#iframe")
          .attr("height",
            $(window).height() - e + "px")
      }
      IS_IPAD = navigator.userAgent.match(/iPad/i) != null;
      $(window).resize(function() {
        e()
      }).resize();
      $("#theme_select").click(function(e) {
        e.stopPropagation();
        $('#theme_list .select').toggleClass('is-open');
      });
      $('html,iframe').click(function() {
        $('#theme_list .select').removeClass('is-open');
      })
      $("#header-bar").hide();
      clicked = "desktop";
      var t = {
        desktop: "100%",
        tabletlandscape: 1040,
        tabletportrait: 788,
        mobilelandscape: 500,
        mobileportrait: 340,
        placebo: 0
      };
      jQuery(".responsive a").on("click", function() {
        var e = jQuery(this);
        for (device in t) {
          console.log(device);
          console.log(t[device]);
          if (e.hasClass(device)) {
            clicked = device;
            jQuery("#iframe").width(t[device]);
            if (clicked == device) {
              jQuery(".responsive a").removeClass("active");
              e.addClass("active")
            }
          }
        }
        return false
      });
      if (IS_IPAD) {
        $("#iframe").css("padding-bottom", "60px")
      }
      $(".post-body").find("img").remove();
    })
    //

b:if.script:
//
    $.ajax({
      type: 'GET',
      url: '/feeds/posts/default',
      data: {
        'max-results': 150,
        'alt': 'json'
      },
      dataType: 'jsonp',
      success: function(k) {
        if (k.feed.entry) {
          for (var t = 0; t < k.feed.entry.length; t++) {
            var e = k.feed.entry[t];
            for (var a = 0; a < e.link.length; a++) {
              if ("alternate" == e.link[a].rel) {
                var s = e.link[a].href;
                break
              }
            }
            var d = e.title.$t;
            if ('category' in e) {            
              for (var r = 0; r < e.category.length; r++) {
                var l = e.category[r].term;
              }
            } else {
              l = "Blogger";
            }
            if ('media$thumbnail' in e) {
              var i = e.media$thumbnail.url.replace("s72-c", "s1600");
            } else {
              var st = e.content.$t,
              at = st.indexOf("<img"),
              bt = st.indexOf('src="', at),
              ct = st.indexOf('"', bt + 5),
              dt = st.substr(bt + 5, ct - bt - 5);
              if (at != -1 && bt != -1 && ct != -1 && dt != "") {
                i = dt;
              } else {
              i = "https://4.bp.blogspot.com/-00O66C-eBQs/W0IcokXSnOI/AAAAAAAAL_k/g4KtDm7SkQsoe7_G0vZ_C_nU0Gf_-kyVQCLcBGAs/s1600/safe_image.png";
              }
            }
            var z = $("link[rel='canonical']").attr('href')
            if (z === s) {
              $('#theme_list ul').append('<li class="button_a current"><a href='+ s +' title="'+ d + '"><span class="title">'+ d +'</span><span class="layout">'+ l +'</span></a><img class="preview" alt="'+ d +'" src='+ i +' /></li>')
              $('#theme_list input').attr('value',d);
            } else {
              $('#theme_list ul').append('<li class="button_a"><a href='+ s +' title="'+ d + '"><span class="title">'+ d +'</span><span class="layout">'+ l +'</span></a><img class="preview" alt="'+ d +'" src='+ i +' /></li>')
            }
          }
        }
      }
    })
