(function(){
  var LABEL = "News";
  var FEED = "/feeds/posts/default/-/" + encodeURIComponent(LABEL) + "?alt=json&max-results=10";

  var trendingWrap = document.getElementById("gtTrendingWrap");
  var heroTitle = document.getElementById("gtHeroTitle");
  var heroImg = document.getElementById("gtHeroImg");
  var heroLink = document.getElementById("gtHeroLink");

  fetch(FEED)
    .then(res => res.json())
    .then(data => {
      trendingWrap.innerHTML = "";
      if(!data.feed || !data.feed.entry) return;

      data.feed.entry.forEach((entry, i) => {
        var title = entry.title.$t;
        var link = entry.link.find(l => l.rel === "alternate").href;

        var img = "";
        if(entry.media$thumbnail){
          img = entry.media$thumbnail.url.replace("s72","s800");
        }

        // === SET HERO PERTAMA ===
        if(i === 0){
          heroTitle.textContent = title;
          heroLink.href = link;
          if(img) heroImg.src = img;
        }

        // === TRENDING ITEM ===
        var a = document.createElement("a");
        a.textContent = title;
        a.href = "javascript:void(0)";
        if(i === 0) a.classList.add("active");

        a.onclick = function(){
          document.querySelectorAll(".gt-trending-list a")
            .forEach(el => el.classList.remove("active"));

          this.classList.add("active");
          heroTitle.textContent = title;
          heroLink.href = link;
          if(img) heroImg.src = img;
        };

        trendingWrap.appendChild(a);
      });
    });
})();
