(function(){

  var heroTitle = document.getElementById("gtHeroTitle");
  var heroImg   = document.getElementById("gtHeroImg");
  var heroLink  = document.getElementById("gtHeroLink");
  var wrap      = document.getElementById("gtnTrendingWrap");
  var tabs      = document.querySelectorAll(".gtn-tab");

  if(!wrap || tabs.length === 0) return;

  function loadTrending(label){
    wrap.innerHTML = "<span class='gtn-loading'>Memuat...</span>";

    var FEED = "/feeds/posts/default/-/" + encodeURIComponent(label) + "?alt=json&max-results=8";

    fetch(FEED)
      .then(function(r){ return r.json(); })
      .then(function(data){
        wrap.innerHTML = "";
        if(!data.feed || !data.feed.entry) return;

        data.feed.entry.forEach(function(entry, i){
          var title = entry.title.$t;
          var link  = entry.link.find(function(l){ return l.rel === "alternate"; }).href;
          var img   = "";

          if(entry.media$thumbnail){
            img = entry.media$thumbnail.url.replace("s72","s800");
          }

          if(i === 0 && heroTitle){
            heroTitle.textContent = title;
            heroLink.href = link;
            if(img && heroImg) heroImg.src = img;
          }

          var a = document.createElement("a");
          a.textContent = title;
          a.href = "javascript:void(0)";
          if(i === 0) a.className = "active";

          a.onclick = function(){
            wrap.querySelectorAll("a").forEach(function(el){
              el.classList.remove("active");
            });
            this.classList.add("active");
            if(heroTitle){
              heroTitle.textContent = title;
              heroLink.href = link;
              if(img && heroImg) heroImg.src = img;
            }
          };

          wrap.appendChild(a);
        });
      });
  }

  tabs.forEach(function(tab){
    tab.onclick = function(){
      tabs.forEach(function(t){ t.classList.remove("active"); });
      this.classList.add("active");
      loadTrending(this.getAttribute("data-label"));
    };
  });

  // INIT
  loadTrending("Nasional");

})();
//

/* Ambil IP */
fetch('https://api.ipify.org?format=json')
  .then(response => response.json())
  .then(data => {
    document.getElementById('ip-address').innerText = data.ip;
  })
  .catch(() => {
    document.getElementById('ip-address').innerText = 'Tidak terdeteksi';
  });

/* Deteksi Browser & OS */
(function () {
  var ua = navigator.userAgent;
  var browser = "Unknown";
  var os = "Unknown";

  if (ua.indexOf("Firefox") > -1) browser = "Mozilla Firefox";
  else if (ua.indexOf("Edg") > -1) browser = "Microsoft Edge";
  else if (ua.indexOf("Chrome") > -1) browser = "Google Chrome";
  else if (ua.indexOf("Safari") > -1) browser = "Safari";
  else if (ua.indexOf("Opera") > -1 || ua.indexOf("OPR") > -1) browser = "Opera";

  if (ua.indexOf("Win") > -1) os = "Windows";
  else if (ua.indexOf("Mac") > -1) os = "MacOS";
  else if (ua.indexOf("Linux") > -1) os = "Linux";
  else if (ua.indexOf("Android") > -1) os = "Android";
  else if (ua.indexOf("like Mac") > -1) os = "iOS";

  document.getElementById("browser-name").innerText = browser;
  document.getElementById("os-name").innerText = os;
})();
