        if ('iswriter' == key) {
          var matches = !!comment.author
              && comment.author.name == config.authorName
              && comment.author.profileUrl == config.authorUrl;
          return matches ? 'true' : '';
        } else if ('deletelink' == key) {
          return config.baseUri + '/comment/delete/'
               + config.blogId + '/' + comment.id;
        } else if ('deleteclass' == key) {
          return comment.deleteclass;
        }
        return '';
      };

      var replybox = null;
      var replyUrlParts = null;
      var replyParent = undefined;

      var onReply = function(commentId, domId) {
        if (replybox == null) {
          // lazily cache replybox, and adjust to suit this style:
          replybox = document.getElementById('comment-editor');
          if (replybox != null) {
            replybox.height = '250px';
            replybox.style.display = 'block';
            replyUrlParts = replybox.src.split('#');
          }
        }
        if (replybox && (commentId !== replyParent)) {
          replybox.src = '';
          document.getElementById(domId).insertBefore(replybox, null);
          replybox.src = replyUrlParts[0]
              + (commentId ? '&parentID=' + commentId : '')
              + '#' + replyUrlParts[1];
          replyParent = commentId;
        }
      };

      var hash = (window.location.hash || '#').substring(1);
      var startThread, targetComment;
      if (/^comment-form_/.test(hash)) {
        startThread = hash.substring('comment-form_'.length);
      } else if (/^c[0-9]+$/.test(hash)) {
        targetComment = hash.substring(1);
      }

      // Configure commenting API:
      var configJso = {
        'maxDepth': config.maxThreadDepth
      };
      var provider = {
        'id': config.postId,
        'data': items,
        'loadNext': paginator,
        'hasMore': hasMore,
        'getMeta': getMeta,
        'onReply': onReply,
        'rendered': true,
        'initComment': targetComment,
        'initReplyThread': startThread,
        'config': configJso,
        'messages': msgs
      };

      var render = function() {
        if (window.goog && window.goog.comments) {
          var holder = document.getElementById('comment-holder');
          window.goog.comments.render(holder, provider);
        }
      };

      // render now, or queue to render when library loads:
      if (window.goog && window.goog.comments) {
        render();
      } else {
        window.goog = window.goog || {};
        window.goog.comments = window.goog.comments || {};
        window.goog.comments.loadQueue = window.goog.comments.loadQueue || [];
        window.goog.comments.loadQueue.push(render);
      }
    })();
//

body.script:
//
let cart = JSON.parse(localStorage.getItem('gosmartCart')) || [];

function updateCartCount() {
    document.getElementById('cartCount').textContent = cart.length;
}

// Function untuk button dengan data attributes
function addToCartFromButton(button) {
    const title = button.getAttribute('data-title');
    const image = button.getAttribute('data-image');

    // Get price dari sibling element
    const productCard = button.closest('.product-card') || button.closest('.single-post');
    const priceElement = productCard.querySelector('.product-price, .product-price-large');
    const priceAttr = priceElement ? priceElement.getAttribute('data-price') : null;
    const price = priceAttr || (priceElement ? priceElement.textContent.trim() : 'Rp0');

    addToCart(title, image, price, button);
}

function addToCart(title, image, price, button) {
    // Check if item already in cart
    const existingItem = cart.find(item => item.title === title);
    if (existingItem) {
        alert('‚úÖ Produk sudah ada di keranjang!');
        toggleCart();
        return;
    }

    // Add to cart
    cart.push({
        title: title,
        image: image || '/favicon.ico',
        price: price,
        id: Date.now()
    });

    // Save to localStorage
    localStorage.setItem('gosmartCart', JSON.stringify(cart));

    // Update UI
    updateCartCount();
    updateCartDisplay();

    // Show feedback
    const originalText = button.innerHTML;
    button.innerHTML = '‚úÖ Ditambahkan';
    button.style.background = '#00b894';
    setTimeout(() => {
        button.innerHTML = originalText;
        button.style.background = '';
    }, 2000);
}

function removeFromCart(id) {
    cart = cart.filter(item => item.id !== id);
    localStorage.setItem('gosmartCart', JSON.stringify(cart));
    updateCartCount();
    updateCartDisplay();
}

function toggleCart() {
    const modal = document.getElementById('cartModal');
    modal.classList.toggle('active');
    updateCartDisplay();
}

function updateCartDisplay() {
    const cartItems = document.getElementById('cartItems');
    const cartFooter = document.getElementById('cartFooter');
    const cartTotal = document.getElementById('cartTotal');

    if (cart.length === 0) {
        cartItems.innerHTML = '<div class="cart-empty"><div class="cart-empty-icon">üõí</div><p>Keranjang belanja Anda kosong</p></div>';
        cartFooter.style.display = 'none';
        return;
    }

    // Calculate total
    let total = 0;
    cart.forEach(item => {
        const priceNum = parseInt(item.price.replace(/[^0-9]/g, ''));
        if (!isNaN(priceNum)) {
            total += priceNum;
        }
    });

    // Display items
    let html = '';
    cart.forEach(item => {
        html += `
            <div class="cart-item">
                <img src="${item.image}" alt="${item.title}" class="cart-item-img" onerror="this.src='/favicon.ico'">
                <div class="cart-item-info">
                    <div class="cart-item-title">${item.title}</div>
                    <div class="cart-item-price">${item.price}</div>
                </div>
                <button class="remove-item" onclick="removeFromCart(${item.id})">Hapus</button>
            </div>
        `;
    });

    cartItems.innerHTML = html;
    cartTotal.textContent = 'Rp' + total.toLocaleString('id-ID');
    cartFooter.style.display = 'block';
}

function goToCheckout() {
    if (cart.length === 0) {
        alert('Keranjang belanja Anda kosong!');
        return;
    }
    localStorage.setItem('gosmartCart', JSON.stringify(cart));
    window.location.href = '/p/checkout.html';
}

// Initialize on page load
updateCartCount();

// Close modal when clicking outside
document.getElementById('cartModal').addEventListener('click', function(e) {
    if (e.target === this) {
        toggleCart();
    }
});
