head.script:
//
var fixedMenu = true,
    fixedSidebar = true,
    relatedPostsNum = 3,
    commentsSystem = "facebook",
    fbCommentsTheme = "light",
    followByEmailText = "",
	darkMode = false;
//

//

var urlTotal,
    nextPageToken,
    postsDatePrefix = false,
    accessOnly = false,
    useApiV3 = false,
    apiKey = "",
    blogId = "",
    postsOrPages = ["pages", "posts"],
    jsonIndex = 1,
    secondRequest = true,
    feedPriority = 0,
    amp = "&";

function urlVal() {
    var e = window.location.pathname,
        t = e.length;
    return e.substring(t - 5) === ".html" ? 0 : t > 1 ? 1 : 2;
}

function urlMod() {
    var e = window.location.pathname;
    if (e.substring(1, 2) === "p") {
        e = e.substring(e.indexOf("/", 1) + 1);
        e = e.substr(0, e.indexOf(".html"));
        history.replaceState(null, null, "../" + e);
    } else {
        e = postsDatePrefix ? e.substring(1) : e.substring(e.indexOf("/", 7) + 1);
        e = e.substr(0, e.indexOf(".html"));
        history.replaceState(null, null, "../../" + e);
    }
}

function urlSearch(e, t) {
    var n = e + ".html";
    t.forEach(function(o) {
        if (o.indexOf(n) !== -1) {
            window.location = o;
        }
    });
}

function urlManager() {
    var e = urlVal();
    if (e === 0) {
        if (!accessOnly) urlMod();
    } else if (e === 1) {
        getJSON(postsOrPages[feedPriority], 1);
    } else if (e === 2) {
        if (!accessOnly) history.replaceState(null, null, "/");
    }
}

function getJSON(e, t) {
    var n = document.createElement("script"),
        o;

    if (useApiV3) {
        o = "https://www.googleapis.com/blogger/v3/blogs/" +
            blogId + "/" + e +
            "?key=" + apiKey +
            "&maxResults=500&fields=nextPageToken,items(url)&callback=bloggerJSON";

        if (nextPageToken) o += "&pageToken=" + nextPageToken;
        nextPageToken = undefined;

    } else {
        o = window.location.protocol + "//" +
            window.location.hostname +
            "/feeds/" + e +
            "/default?start-index=" + t +
            "&max-results=150&orderby=published&alt=json-in-script&callback=bloggerJSON";
    }

    o = o.replace(/#/g, amp);
    n.type = "text/javascript";
    n.src = o;
    document.getElementsByTagName("head")[0].appendChild(n);
}

function bloggerJSON(e) {
    var list = [];

    if (useApiV3 || urlTotal === undefined) {
        try {
            urlTotal = parseInt(e.feed.openSearch$totalResults.$t);
        } catch (err) {}
    }

    if (useApiV3) {
        try {
            e.items.forEach(function(x) { list.push(x.url); });
        } catch (err) {}
        nextPageToken = e.nextPageToken;

    } else {
        try {
            e.feed.entry.forEach(function(entry) {
                entry.link.forEach(function(l) {
                    if (l.rel === "alternate") list.push(l.href);
                });
            });
        } catch (err) {}
    }

    urlSearch(window.location.pathname, list);

    if (urlTotal > 150) {
        jsonIndex += 150;
        urlTotal -= 150;
        getJSON(postsOrPages[feedPriority], jsonIndex);

    } else if (nextPageToken) {
        getJSON(postsOrPages[feedPriority]);

    } else if (secondRequest) {
        nextPageToken = undefined;
        urlTotal = undefined;
        jsonIndex = 1;
        secondRequest = false;

        if (feedPriority === 0) {
            feedPriority = 1;
            getJSON("posts", 1);
        } else {
            feedPriority = 0;
            getJSON("pages", 1);
        }
    }
}

function bloggerJS(e) {
    if (e) feedPriority = e;
    urlManager();
}

bloggerJS();

