          <!-- GueTemenin: Cookie popup + Reject Close/Redirect (Shadow DOM) -->
<script>
(function () {
  const ROOT_ID = 'gt-cookie-root';

  // Stop if already accepted
  try {
    if (localStorage.getItem('gt_cookieAccepted') === 'true') return;
  } catch (e) {}

  // Fallback cookie helper
  function setCookie(n, v, d) {
    const t = new Date();
    t.setTime(t.getTime() + d*24*60*60*1000);
    document.cookie = n+"="+encodeURIComponent(v)+";expires="+t.toUTCString()+";path=/";
  }
  function getCookie(n) {
    return document.cookie.split(';').map(s=>s.trim()).find(x=>x.startsWith(n+"="))?.split('=')[1] || "";
  }

  if (getCookie('gt_cookieAccepted') === 'true') return;
  if (document.getElementById(ROOT_ID)) return;

  const host = document.createElement('div');
  host.id = ROOT_ID;
  host.style.all = 'initial';
  document.documentElement.appendChild(host);

  const shadow = host.attachShadow ? host.attachShadow({mode:"closed"}) : host;

  const tpl = document.createElement('div');
  tpl.innerHTML = `
    <style>
      :host { all: initial; }
      .overlay {
        position: fixed; inset: 0;
        display: flex; align-items: center; justify-content: center;
        background: rgba(0,0,0,.55);
        z-index: 2147483647;
        font-family: system-ui, Arial;
      }
      .card {
        width: 92vw; max-width: 460px;
        background: #fff; border-radius: 12px;
        box-shadow: 0 18px 50px rgba(8,8,20,.35);
        padding: 20px; box-sizing: border-box;
        color: #222; line-height: 1.45; text-align: left;
        animation: slideIn .26s cubic-bezier(.2,.9,.2,1);
      }
      @keyframes slideIn {
        from { opacity: 0; transform: translateY(18px) scale(.98); }
        to   { opacity: 1; transform: translateY(0) scale(1); }
      }
      .title { font-size: 16px; font-weight: 700; margin-bottom: 8px; }
      .desc { font-size: 15px; margin-bottom: 16px; }
      .desc a { color: #e91e63; font-weight:600; }
      .actions { display:flex; gap:10px; }
      button {
        border:none; cursor:pointer; padding:10px 12px;
        border-radius:8px; font-size:15px; font-weight:700;
      }
      .accept { background:#ffcc00; flex:1; }
      .reject { background:#fff; border:1px solid #ddd; flex:1; }
      .close-x {
        position:absolute; top:12px; right:14px;
        background:transparent; border:none;
        font-size:18px; cursor:pointer; color:#666;
      }
    </style>

    <div class="overlay">
      <div class="card">
        <button class="close-x">âœ•</button>
        <h3 class="title">Kami menggunakan cookies</h3>
        <p class="desc">ðŸª Situs ini menggunakan cookies untuk meningkatkan pengalaman. Dengan melanjutkan, Anda setuju dengan <a href="/p/cookies.html" target="_blank">Kebijakan Cookies</a>.</p>
        <div class="actions">
          <button class="reject">Tolak</button>
          <button class="accept">Setuju</button>
        </div>
      </div>
    </div>
  `;

  (shadow || host).appendChild(tpl);

  const overlay = (shadow || host).querySelector('.overlay');
  const acceptBtn = (shadow || host).querySelector('.accept');
  const rejectBtn = (shadow || host).querySelector('.reject');
  const closeX = (shadow || host).querySelector('.close-x');

  const prevOverflow = document.documentElement.style.overflow;
  document.documentElement.style.overflow = 'hidden';

  function closeAndSave(consent) {
    try {
      if (consent) {
        try { localStorage.setItem('gt_cookieAccepted', 'true'); } 
        catch(e){ setCookie('gt_cookieAccepted','true',365); }
      } else {
        try { localStorage.setItem('gt_cookieAccepted', 'false'); } 
        catch(e){ setCookie('gt_cookieAccepted','false',365); }
      }
    } catch (e){}

    overlay.remove();
    host.remove();
    document.documentElement.style.overflow = prevOverflow || '';
  }

  // ==== CUSTOM: ACTION KETIKA MENOLAK ====
  function onReject() {
    closeAndSave(false);

    // 1. coba close tab
    window.open('', '_self');
    window.close();

    // 2. fallback redirect jika close diblokir
    setTimeout(() => {
      window.location.href = "/p/penolakan.html";
    }, 200);
  }

  acceptBtn.onclick = () => closeAndSave(true);
  rejectBtn.onclick = () => onReject();

  // Tombol X ikut dianggap MENOLAK atau SETUJU?
  // Jika mau X = tolak â†’ ganti ke: onReject();
  closeX.onclick = () => closeAndSave(true);

})();
</script>

















