function getFeedUrl(e, t, a) {
    var r = "";
    switch (a) {
    case "recent":
        r = "/feeds/posts/default?alt=json&max-results=" + t;
        break;
    case "comments":
        r = "list1" == e ? "/feeds/comments/default?alt=json&max-results=" + t : "/feeds/posts/default/-/" + a + "?alt=json&max-results=" + t;
        break;
    default:
        r = "/feeds/posts/default/-/" + a + "?alt=json&max-results=" + t
    }
    return r
}

function getPostLink(e, t) {
    for (var a = 0; a < e[t].link.length; a++)
        if ("alternate" == e[t].link[a].rel) {
            var r = e[t].link[a].href;
            break
        }
    return r
}

function getPostTitle(e, t) {
    if (e[t].title.$t) var a = e[t].title.$t;
    else a = messages.noTitle;
    return a
}

function getFirstImage(e, t) {
    var a = $("<div>").html(e).find("img:first").attr("src"),
        r = a.lastIndexOf("/") || 0,
        i = a.lastIndexOf("/", r - 1) || 0,
        n = a.substring(0, i),
        s = a.substring(i, r),
        o = a.substring(r);
    return (s.match(/\/s[0-9]+/g) || s.match(/\/w[0-9]+/g) || "/d" == s) && (s = "/w300-h300-p-k-no-nu"), n + s + o
}

function getPostImage(e, t, a) {
    var r = e[t].content.$t;
    if (e[t].media$thumbnail) var i = e[t].media$thumbnail.url;
    else i = "https://i.imgur.com/nEd31ej.png";
    return r.indexOf(r.match(/<iframe(?:.+)?src=(?:.+)?(?:www.youtube.com)/g)) > -1 ? r.indexOf("<img") > -1 ? r.indexOf(r.match(/<iframe(?:.+)?src=(?:.+)?(?:www.youtube.com)/g)) < r.indexOf("<img") ? i.replace("/default.", "/maxresdefault.") : getFirstImage(r) : i.replace("/default.", "/maxresdefault.") : r.indexOf("<img") > -1 ? getFirstImage(r) : "https://i.imgur.com/nEd31ej.png"
}

