function getAjax(e, t, a, r) {
    switch (t) {
    case "related":
        0 == r && (r = "geterror404");
        var i = getFeedUrl(t, a, r);
        $.ajax({
            url: i,
            type: "GET",
            dataType: "json",
            cache: !0,
            beforeSend: function (a) {
                switch (t) {
                case "related":
                    e.html(beforeLoader()).parent().addClass("show-ify")
                }
            },
            success: function (a) {
                var r = "";
                switch (t) {
                case "related":
                    r = '<div class="related-posts">'
                }
                var i = a.feed.entry;
                if (null != i)
                    for (var n = 0, s = i; n < s.length; n++) {
                        var o = getPostLink(s, n),
                            l = getPostTitle(s, n, o),
                            c = getPostImage(s, n, o),
                            d = (getPostLabel(s, n), getVideoClass(c)),
                            h = "";
                        switch (t) {
                        case "related":
                            h += '<article class="related-item post item-' + n + '"><a class="entry-image-wrap ' + d + '" href="' + o + '"><span class="entry-thumb" data-image="' + c + '"/></a><div class="entry-header"><h2 class="entry-title"><a href="' + o + '">' + l + "</a></h2></div></article>"
                        }
                        r += h
                    } else r = msgError();
                r += "</div>", e.html(r), e.find("span.entry-thumb").lazyify()
            },
            error: function () {
                e.html(msgError())
            }
        })
    }
}

function ajaxRelated(e, t, a, r, i) {
    if (i.match("getrelated")) return getAjax(e, t, a, r)
}

