var xinhteen_feeds = 0;

function xinhteen_load_cate() {
    if (0 == xinhteen_feeds) {
        xinhteen_feeds = 1;
        var e = $(".feed-entry");
        "" != e.length && $(e).each(function (t) {
            var a = $(e[t]).find(".widget-title"),
                l = Number($(a).attr("data-item")),
                n = $(a).attr("data-layout");
            if (null != $(a).attr("data-category")) var o = $(a).attr("data-category");
            else o = "";
            var r = `/search/label/${o}?max-results=8`;
            $.get(r, function (a) {
                var o = $(a).find("article.blog-post");
                if (o.length) {
                    var r = o.length;
                    o.length < l && (l = o.length);
                    let a = [];
                    do {
                        let e = Math.floor(Math.random() * r);
                        a.push(e), a = a.filter((e, t) => a.indexOf(e) === t)
                    } while (a.length < l);
                    a.forEach(function (a) {
                        if ("grid" == n) {
                            let n = $(o[a]).find("span.entry-thumb");
                            for (let e = 0; e < n.length; e++) {
                                const t = n[e];
                                $(t).addClass("lazy-ify"), $(t).attr("style", "background-image:url(" + $(t).attr("data-image").replace("=w72-h72-p-k-no-nu", "").replace("w72-h72-p-k-no-nu", "w245-h150-p-k-no-nu") + ")")
                            }
                            var l = '<article class="blog-post hentry index-post">' + $(o[a]).html() + "</article>";
                            $(e[t]).find(".spinner").removeClass("spinner"), $(e[t]).find(".row").append(l)
                        } else console.log("design5s")
                    })
                }
            }, "html")
        })
    }
    $(".published").timeago()
}
window.addEventListener("scroll", function () {
    xinhteen_load_cate()
}), $("html").each(function () {
    var e = $(this);
    1 != darkMode && (e.attr("data-theme", localStorage.themeColor), $(".darkmode-toggle,.mobile-darkmode-toggle").on("click", function () {
