(function(){
  var loc = (location.pathname || "").toLowerCase();

  try {
    if (loc.includes("agen-pria")) {
      document.getElementById("nav-pria").classList.add("active");
    } 
    else if (loc.includes("agen-perempuan")) {
      document.getElementById("nav-perempuan").classList.add("active");
    } 
    else if (loc.includes("akun")) {
      document.getElementById("nav-akun").classList.add("active");
    } 
    else {
      document.getElementById("nav-home").classList.add("active");
    }
  } catch(e){
    console.log("Nav error:", e);
  }
})();
