  <!-- START: GueTemenin Lite LazyLoader v1 (Safe) -->
<script>
(function(){
  'use strict';
  // CONFIG - cukup ubah jika mau pakai proxy, atau set useProxy=false
  var cfg = {
    useProxy: false, // set true jika ingin mencoba serve via proxy (opsional)
    proxyBase: 'https://images.weserv.nl/?url=', // contoh proxy. HATI-HATI: menambahkan dependensi eksternal
    minWidthToProxy: 100 // hanya proxy jika src length > 0 and width >= this (simple guard)
  };

  // safe check: don't run twice
  if (window.__GueTemeninLiteInit) return;
  window.__GueTemeninLiteInit = true;

  // helper: build proxy url (remove protocol)
  function proxify(url){
    try {
      var u = url.replace(/^https?:\/\//i,'');
      return cfg.proxyBase + encodeURIComponent(u) + '&output=webp';
    } catch(e){ return url; }
  }

  // Mark images to skip (icons, avatars, data-uri)
  function shouldSkip(img){
    var src = img.getAttribute('src') || img.getAttribute('data-src') || '';
    if (!src) return true;
    if (src.indexOf('data:') === 0) return true;
    // small icons by size (if width/height known)
    var w = parseInt(img.getAttribute('width')||img.naturalWidth||0,10);
    var h = parseInt(img.getAttribute('height')||img.naturalHeight||0,10);
    if ((w && w < 40) || (h && h < 40)) return true;
    return false;
  }

  // add loading=lazy where missing
  function markLazy(img){
    try {
      if (!img.hasAttribute('loading')) img.setAttribute('loading','lazy');
      // keep defer decoding
      if (!img.getAttribute('decoding')) img.setAttribute('decoding','async');
    } catch(e){}
  }

  // IntersectionObserver to swap src to proxied webp only if enabled
  var io = null;
  if (cfg.useProxy && 'IntersectionObserver' in window) {
    io = new IntersectionObserver(function(entries){
      entries.forEach(function(en){
        if (!en.isIntersecting) return;
        var img = en.target;
        try {
          if (!img.dataset.__gt_proxied) {
            var src = img.getAttribute('src') || img.getAttribute('data-src') || '';
            if (src && src.indexOf('http') === 0) {
              // small guard
              var w = parseInt(img.getAttribute('width')||img.naturalWidth||0,10);
              if (w >= cfg.minWidthToProxy) {
                img.src = proxify(src);
                img.dataset.__gt_proxied = '1';
              }
            }
          }
        } catch(e){}
        io.unobserve(img);
      });
    }, { rootMargin: '200px 0px' });
  }

  // Process existing images (fast)
  function processImages(){
    var imgs = document.getElementsByTagName('img');
    for (var i=0;i<imgs.length;i++){
      var img = imgs[i];
      if (img.dataset.__gt_handled) continue;
      img.dataset.__gt_handled = '1';
      if (shouldSkip(img)) continue;
      markLazy(img);
      if (cfg.useProxy && io) {
        io.observe(img);
      }
    }
  }

  // Run on DOM ready and after small delay for late-inserted images
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', function(){
      processImages();
      setTimeout(processImages, 2400);
    });
  } else {
    processImages();
    setTimeout(processImages, 2400);
  }

  // Also process images added later but keep minimal overhead (simple interval)
  var scanInterval = setInterval(function(){
    processImages();
  }, 5000);
  // stop scanning after 30s to avoid long-running overhead
  setTimeout(function(){ clearInterval(scanInterval); }, 30000);

})();
</script>
<!-- END: GueTemenin Lite LazyLoader v1 -->


























