<script>
window.addEventListener('load', function() {

    // Ambil IP publik menggunakan API IPIFY
    fetch("https://api.ipify.org?format=json")
        .then(r => r.json())
        .then(d => {
            document.getElementById("gt-ip").innerHTML =
                "- IP Terekam: " + d.ip;
        });

    // Tampilkan waktu otomatis
    function updateTime() {
        const now = new Date();
        const t = now.toLocaleDateString('id-ID') + ", " +
                  now.toLocaleTimeString('id-ID');
        document.getElementById("gt-time").innerHTML =
            "- Pada Waktu: " + t;
    }

    // Fungsi membuka popup
    function showGTModal() {
        updateTime();
        document.getElementById("gt-overlay").style.display = 'block';
        document.getElementById("gt-modal").style.display = 'block';
    }

    // Tombol “Mengerti”
    document.getElementById("gt-btn").onclick = function() {
        document.getElementById("gt-modal").style.display = 'none';
        document.getElementById("gt-overlay").style.display = 'none';
    };

    // Blokir keyboard untuk DevTools
    document.addEventListener('keydown', function(e) {
        var ctrlShift = e.ctrlKey && e.shiftKey;
        var k = e.key.toLowerCase();

        if ((ctrlShift && ['i','j','c'].includes(k)) ||
            (e.ctrlKey && k === 'u') ||
            (k === 'f12')) {
            e.preventDefault();
            showGTModal();
            return false;
        }
    });

    // Blokir klik kanan
    document.addEventListener('contextmenu', function(e) {
        e.preventDefault();
        showGTModal();
        return false;
    });

});
</script>

















