// Deteksi DevTools (F12 / Ctrl+Shift+I / Cmd+Option+I)
document.addEventListener('keydown', function(e) {
    if(e.keyCode==123 || (e.ctrlKey && e.shiftKey && e.keyCode==73)) {
        showSecurityModal2();
        e.preventDefault();
    }
});

// Deteksi PrintScreen
document.addEventListener('keyup', function(e) {
    if(e.key === "PrintScreen") {
        showSecurityModal2();
        alert("Screenshot terdeteksi! Data dilindungi.");
    }
});

// Deteksi shortcut screenshot (Windows+Shift+S, Mac Cmd+Shift+4/5)
document.addEventListener('keydown', function(e) {
    if((e.keyCode===83 && e.shiftKey && e.metaKey) || (e.keyCode===52 && e.shiftKey && e.metaKey)) {
        showSecurityModal2();
    }
});

// Nonaktifkan klik kanan
document.addEventListener("contextmenu", function(e){
    e.preventDefault();
    alert("Kami menonaktifkan klik kanan dan sejenisnya!");
});

// Nonaktifkan seleksi teks
document.addEventListener("selectstart", function(e){
    e.preventDefault();
});
</script>









<script>
  window.addEventListener('load', function() {
    // Pastikan hanya redirect dari domain utama
    const currentDomain = window.location.hostname.toLowerCase();
    if (currentDomain !== "www.guetemenin.web.id" && currentDomain !== "guetemenin.web.id") {
      return; // keluar jika bukan domain utama
    }

    const path = window.location.pathname.toLowerCase();

    // Mapping path ke subdomain
    const redirects = [
      { paths: [/^\/(register|daftar|masuk|login)/], url: "https://agen.guetemenin.web.id/?m=1" },
      { paths: [/^\/(client|klien)/], url: "https://client.guetemenin.web.id/?m=1" },
      { paths: [/^\/(member|dashboard)/], url: "https://member.guetemenin.web.id/?m=1" },
      { paths: [/^\/admin/], url: "https://admin.guetemenin.web.id/?m=1" },
      { paths: [/^\/news/], url: "https://news.guetemenin.web.id/?m=1" },
      { paths: [/^\/faq/], url: "https://faq.guetemenin.web.id/?m=1" },
      { paths: [/^\/chat/], url: "https://chat.guetemenin.web.id/?m=1" },
      { paths: [/^\/services/], url: "https://services.guetemenin.web.id/?m=1" },
      { paths: [/^\/pay/], url: "https://pay.guetemenin.web.id/?m=1" },
      { paths: [/^\/support/], url: "https://support.guetemenin.web.id/?m=1" },
      { paths: [/^\/api/], url: "https://api.guetemenin.web.id/?m=1" },
      { paths: [/^\/files/], url: "https://files.guetemenin.web.id/?m=1" },
      { paths: [/^\/auth/], url: "https://auth.guetemenin.web.id/?m=1" },
      { paths: [/^\/promo/], url: "https://promo.guetemenin.web.id/?m=1" },
      { paths: [/^\/portal/], url: "https://portal.guetemenin.web.id/?m=1" }
    ];

    // Lakukan redirect jika path sesuai
    for (let i = 0; i < redirects.length; i++) {
      const redirect = redirects[i];
      for (let j = 0; j < redirect.paths.length; j++) {
        if (redirect.paths[j].test(path)) {
          window.location.replace(redirect.url);
          return; // hentikan setelah redirect
        }
      }
    }
  });
</script>
