(function () {
  const items = document.querySelectorAll(
    '.gt-award img, .gt-app-btn img'
  );

  items.forEach(el => {
    el.addEventListener('mouseenter', () => {
      el.style.opacity = '1';
    });
    el.addEventListener('mouseleave', () => {
      el.style.opacity = '0.95';
    });
  });
})();

