(function(){
var btn = document.getElementById("gtnMenuBtn");
var nav = document.getElementById("gtnOffcanvas");
var ov = document.getElementById("gtnOverlay");
var cls = document.getElementById("gtnCloseBtn");

if(!btn || !nav) return;

function openMenu(){
nav.classList.add("active");
ov.classList.add("active");
}

function closeMenu(){
nav.classList.remove("active");
ov.classList.remove("active");
}

btn.onclick = openMenu;
cls.onclick = closeMenu;
ov.onclick = closeMenu;
})();
