//
(function(){
var LABEL = "News";
var feedUrl = "/feeds/posts/default/-/" + encodeURIComponent(LABEL) + "?alt=json&max-results=10";

fetch(feedUrl)
.then(function(res){ return res.json(); })
.then(function(data){
var wrap = document.getElementById("gtTrendingWrap");
wrap.innerHTML = "";

if(!data.feed || !data.feed.entry){
wrap.innerHTML = "<span class='gt-loading'>Belum ada data</span>";
return;
}

data.feed.entry.forEach(function(entry, index){
var title = entry.title.$t;
var link = "";

for(var i=0;i<entry.link.length;i++){
if(entry.link[i].rel === "alternate"){
link = entry.link[i].href;
break;
}
}

var a = document.createElement("a");
a.href = link;
a.textContent = title;

// item pertama aktif
if(index === 0){
a.classList.add("active");
}

// klik ‚Üí aktif pindah
a.addEventListener("click", function(){
var all = wrap.querySelectorAll("a");
all.forEach(function(el){
el.classList.remove("active");
});
this.classList.add("active");
});

wrap.appendChild(a);
});

// AUTO SCROLL
setInterval(function(){
wrap.scrollLeft += 220;
if(wrap.scrollLeft >= wrap.scrollWidth - wrap.clientWidth){
wrap.scrollLeft = 0;
}
}, 4000);
})
.catch(function(){
document.getElementById("gtTrendingWrap").innerHTML =
"<span class='gt-loading'>Gagal memuat</span>";
});
})();
//

(function () {
  var label = document.querySelector('a[rel="tag"]');
  if (!label) return;

  var feedUrl = '/feeds/posts/default/-/' + label.textContent + '?alt=json&max-results=5';

  fetch(feedUrl)
    .then(res => res.json())
    .then(data => {
      var list = document.getElementById('gt-related-list');
      if (!list) return;

      data.feed.entry.forEach(post => {
        var title = post.title.$t;
        var link = post.link.find(l => l.rel === 'alternate').href;

        var li = document.createElement('li');
        li.innerHTML = `${title}`;
        list.appendChild(li);
      });
    });
})();

//
(function(){

var heroTitle = document.getElementById("gtHeroTitle");
var heroImg = document.getElementById("gtHeroImg");
var heroLink = document.getElementById("gtHeroLink");
var wrap = document.getElementById("gtnTrendingWrap");
var tabs = document.querySelectorAll(".gtn-tab");

if(!wrap || tabs.length === 0) return;

function loadTrending(label){
wrap.innerHTML = "<span class='gtn-loading'>Memuat...</span>";

var FEED = "/feeds/posts/default/-/" + encodeURIComponent(label) + "?alt=json&max-results=8";

fetch(FEED)
.then(function(r){ return r.json(); })
.then(function(data){
wrap.innerHTML = "";
if(!data.feed || !data.feed.entry) return;

data.feed.entry.forEach(function(entry, i){
var title = entry.title.$t;
var link = entry.link.find(function(l){ return l.rel === "alternate"; }).href;
var img = "";

if(entry.media$thumbnail){
img = entry.media$thumbnail.url.replace("s72","s800");
}

if(i === 0 && heroTitle){
heroTitle.textContent = title;
heroLink.href = link;
if(img && heroImg) heroImg.src = img;
}

var a = document.createElement("a");
a.textContent = title;
a.href = "javascript:void(0)";
if(i === 0) a.className = "active";

a.onclick = function(){
wrap.querySelectorAll("a").forEach(function(el){
el.classList.remove("active");
});
this.classList.add("active");
if(heroTitle){
heroTitle.textContent = title;
heroLink.href = link;
if(img && heroImg) heroImg.src = img;
}
};

wrap.appendChild(a);
});
});
}

tabs.forEach(function(tab){
tab.onclick = function(){
tabs.forEach(function(t){ t.classList.remove("active"); });
this.classList.add("active");
loadTrending(this.getAttribute("data-label"));
};
});

// INIT
loadTrending("Nasional");

})();
