fetch('https://cnnaie.blogspot.com/feed.json')
.then(res => res.json())
.then(data => {
  let html = '<div class="widget-grid">';
  data.posts.forEach(post => {
    html += `<div class="card"><a href="${post.url}"><img src="${post.thumb}"><h4>${post.title}</h4></a></div>`;
  });
  html += '</div>';
  document.getElementById('widget-container').innerHTML = html;
});



(function(){
  var feedUrl = "/feeds/posts/default?alt=json&max-results=10";

  fetch(feedUrl)
    .then(function(res){ return res.json(); })
    .then(function(data){
      var wrap = document.getElementById("gtTrendingWrap");
      wrap.innerHTML = "";

      if(!data.feed.entry){ return; }

      data.feed.entry.forEach(function(entry){
        var title = entry.title.$t;
        var link = "";

        entry.link.forEach(function(l){
          if(l.rel === "alternate"){ link = l.href; }
        });

        var a = document.createElement("a");
        a.href = link;
        a.textContent = title;
        wrap.appendChild(a);
      });

      // auto scroll
      setInterval(function(){
        wrap.scrollLeft += 200;
        if(wrap.scrollLeft >= wrap.scrollWidth - wrap.clientWidth){
          wrap.scrollLeft = 0;
        }
      }, 4000);
    });
})();






