(function () {
  var btn = document.getElementById('gtScrollTop');
  if (!btn) return;

  window.addEventListener('scroll', function () {
    if (window.pageYOffset > 300) {
      btn.style.display = 'block';
    } else {
      btn.style.display = 'none';
    }
  });

  btn.addEventListener('click', function () {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  });
})();



function gtOpenMenu(){
  document.getElementById('gtSideMenu').style.right = '0';
  document.getElementById('gtOverlay').style.display = 'block';
}

function gtCloseMenu(){
  document.getElementById('gtSideMenu').style.right = '-280px';
  document.getElementById('gtOverlay').style.display = 'none';
}

(function () {
  const searchBox = document.querySelector('.gt-search');
  if (!searchBox) return;

  const btn = searchBox.querySelector('.gt-search-btn');
  const input = searchBox.querySelector('input');

  btn.addEventListener('click', function () {
    searchBox.classList.toggle('active');
    if (searchBox.classList.contains('active')) {
      setTimeout(() => input.focus(), 100);
    }
  });

  document.addEventListener('click', function (e) {
    if (!searchBox.contains(e.target)) {
      searchBox.classList.remove('active');
    }
  });
})();

