$('#id-menu-desktop').each(function() {
  var iTms = $(this).find('.LinkList ul > li').children('a'),
    iLen = iTms.length;
  for (var i = 0; i < iLen; i++) {
    var i1 = iTms.eq(i),
      t1 = i1.text();
    if (t1.charAt(0) !== '_') {
      var i2 = iTms.eq(i + 1),
        t2 = i2.text();
      if (t2.charAt(0) === '_') {
        var l1 = i1.parent();
        l1.append('<ul class="dropdown-menu"/>');
        i1.append('<i class="fa fa-angle-down"></i>')
        $("#id-menu-desktop ul li.dropdown>a").addClass("dropdown-toggle")
        l1.addClass("dropdown")
      }
    }
    if (t1.charAt(0) === '_') {
      i1.text(t1.replace('_', ''));
      i1.parent().appendTo(l1.children('.dropdown-menu'))

    }
  }
})
