function productsearch(e) {
  for (var n = 0; n < e.feed.entry.length; n++) {
    var entry = e.feed.entry[n];
    for (var s = 0; s < e.feed.entry[n].link.length; s++) {
      if (e.feed.entry[n].link[s].rel === "alternate") {
        t = e.feed.entry[n].link[s].href;
        break
      }
    }
    var giacu = '';
    var giaban = '';
    if ("media$thumbnail" in e.feed.entry[n]) {
      d = e.feed.entry[n].media$thumbnail.url.replace("s72-c", "w480-h480-p-k-no-nu")
    } else {
      d = "https://3.bp.blogspot.com/-Yw8BIuvwoSQ/VsjkCIMoltI/AAAAAAAAC4c/s55PW6xEKn0/w240-h240-p-k-no-nu/nth.png"
    }
    if ("media$thumbnail" in e.feed.entry[n]) {
      tin = e.feed.entry[n].media$thumbnail.url.replace("s72-c", "w480-h240-p-k-no-nu")
    } else {
      tin = "https://3.bp.blogspot.com/-Yw8BIuvwoSQ/VsjkCIMoltI/AAAAAAAAC4c/s55PW6xEKn0/w240-h240-p-k-no-nu/nth.png"
    }
    for (var c = 0; c < entry.link.length; c++) {
      if (entry.link[c].rel == 'enclosure') {
        var entry_enclosure = entry.link[c].href;
        if (entry_enclosure == "http://giaban.com") {
          giaban += entry.link[c].type
        }
        if (entry_enclosure == "http://giacu.com") {
          if (entry.link[c].type != "empty" && entry.link[c].type != "image/jpeg" && entry.link[c].type != "image/png") {
            giacu = entry.link[c].type;
            var price = giaban.replace(/[^0-9\.]+/ig, "");
            var old = giacu.replace(/[^0-9\.]+/ig, "");
            var giam = (100 * ((parseInt(price) - parseInt(old)) / parseInt(old))).toFixed(0);
            var sale = '<div class="sale-label sale-top-right">'+giam+'%</div>';
          } else {
            giacu = sale = '';
          }
        }
      }
    }
    var f = e.feed.entry[n].title.$t;
    if ("content" in e.feed.entry[n]) var post = e.feed.entry[n].content.$t;
    else post = "summary" in e.feed.entry[n] ? e.feed.entry[n].summary.$t : "";
    var post_content = post.replace(/<\S[^>]*>/g, "");
    var chuoi = e.feed.entry[n].published.$t,
      nam = chuoi.substring(0, 4),
      thang = chuoi.substring(5, 7),
      ngay = chuoi.substring(8, 10),
      date = ngay + "." + thang + "." + nam,
      author = e.feed.entry[n].author[0].name.$t,
      cmt = e.feed.entry[n].thr$total.$t;
    if (giaban.length != 0) {
      var r = '<div class="col-md-3 col-sm-6 col-xs-6 product-item"> <div class="product-box"> <div class="product-image"> <a href="'+t+'"> <img src="'+d+'" alt="'+f+'"></a> '+sale+' </div> <h4 class="product-name"> <a href="'+t+'">'+f+'</a> </h4><span class="product-price"><span class="old_price">'+giacu+'</span> '+giaban+'</span> </div> </div>'
    }
    if (giaban.length == 0) {
      var r = '<div class="col-lg-12 col-md-12 col-sm-12 col-xs-12 news_post_loop"> <div class="row"> <div class="col-lg-5 col-md-5 col-sm-12 col-xs-12"> <div class="news_post_loop_img"><a href="'+t+'"><img src="'+tin+'" alt="'+f+'"></a></div> </div> <div class="col-lg-7 col-md-7 col-sm-12 col-xs-12"> <div class="news_post_loop_title"> <h4><a href="'+t+'">'+f+'</a></h4> </div> <div class="news_post_loop_info"> <p class="cl_old"><span><i class="fa fa-user" aria-hidden="true"></i> '+author+'</span> <span><i class="fa fa-calendar" aria-hidden="true"></i> '+date+'</span></p> </div> <div class="news_post_loop_content cl_old">' + post_content.substring(0, 120) + '...</div> <div class="news_post_loop_more"><a href="'+t+'" class="site-button-dark"><span>Read more &gt;&gt; </span></a></div> </div> </div> </div>'
    }
    $('.multi-columns-row').append(r)
  }
}
$.ajax({
  url: "/feeds/posts/default/-/" + labeldata,
  type: "get",
  data: {
    alt: "json",
    "max-results": numPost
  },
  dataType: "jsonp",
  jsonpCallback: "productsearch",
  beforeSend: function() {
    $('#content').addClass('loadinglabel')
  },
  success: function(e) {
    $('#content').removeClass('loadinglabel');
    total = e.feed.openSearch$totalResults.$t;
    count += e.feed.entry.length;
    $('#loadmore').append('<div class="blog-pager" id="blog-pager"> <span class="load-more" id="load-more-link">Load more</span> <span class="loading"><span class="loader"></span></span> </div>');
    $('.islabelsearch').removeClass('load');
    if (numPost >= total) {
      $("#blog-pager").hide()
    }
    if (count < total) {
      $("#load-more-link").on("click", function(e) {
        e.preventDefault();
        var a = $(this);
        count < total && ($("#blog-pager .loading").show(), a.hide(), setTimeout(function() {
          $.ajax({
            url: "/feeds/posts/default/-/" + labeldata,
            type: "get",
            data: {
              alt: "json",
              "max-results": numPost,
              "start-index": count + 1
            },
            dataType: "jsonp",
            jsonpCallback: "productsearch",
            success: function(e) {
              a.show();
              $("#blog-pager .loading").hide();
              (count += e.feed.entry.length) == total && $("#load-more-link").hide()
            }
          })
        }, 100))
      })
    }
  }
})
